def expand_all():
    path = '/Users/suyan/clawd/projects/gospel-app/web/src/data/apologetics.json'
    with open(path, 'r', encoding='utf-8') as f:
        data = json.load(f)
    
    expansions = get_expansions()
    
    for item in data:
        item_id = item['id']
        if item_id in expansions:
            exp = expansions[item_id]
            if 'core_argument_zh' in exp:
                item['core_argument_zh'] = exp['core_argument_zh']
            if 'core_argument_en' in exp:
                item['core_argument_en'] = exp['core_argument_en']
            if 'extra_objection' in exp:
                item['common_objections'].append(exp['extra_objection'])
    
    with open(path, 'w', encoding='utf-8') as f:
        json.dump(data, f, ensure_ascii=False, indent=2)
    
    print("Expanded articles written.")

